    public void cargar_Home(){
        Home h = new Home();
        for(int i=0;i<articulos.size();i++){
            Articulo a = articulos.get(i);
            
            //Llenamos el Jpanel del articulo (Excepto Imagenes)
            PArticulo pa = new PArticulo();
            pa.setJA_Nombre(a.getAlias());
            pa.setJA_Desc(a.getDescripcion());
            pa.setJA_Cant(Integer.toString(a.getCantidad()));
            
            //Obtenemos el Label donde irá el icono1
            JLabel aux = pa.getJA_img1();
            
            //Guardamos el icono
            aux.setIcon(new Utils().redimensionar(a.getB64imagenFr(),75,75));
            
            pa.setJA_img1(aux);
            
            //Obtenemos el Label donde irá el icono2
            aux = pa.getJA_img2();
            
            aux.setIcon(new Utils().redimensionar(a.getB64imagenLt(),75,75));
            
            pa.setJA_img2(aux);
            
            System.out.println(a.toString());
            
            //Enlazamos todo el jpanel a nuestro panel de home
            pa.setSize(200, 250);
            pa.setLocation(5*40*i,5);
            pa.setBorder(BorderFactory.createLineBorder(Color.black));
            h.add(pa, BorderLayout.CENTER);
            h.revalidate();
            h.repaint();
        }
        h.setLocation(0,0);
        h.setSize(650, 300);
        PPrincipal.removeAll();
        PPrincipal.add(h, BorderLayout.CENTER);
        PPrincipal.revalidate();
        PPrincipal.repaint();
    }

